import React, { useState, useEffect, useContext } from 'react'
import "./Picker.css";
import ButtonBack from '../buttons/ButtonBack'
import iconFeladat from '../../assets/iconFeladat.png'

let feladatList = []
let element

function Picker(props) {

  const makeFeladatList = () => {
    feladatList.length = 0
    feladatList.push('feladat1')
    feladatList.push('feladat2')
    feladatList.push('feladat3')
    feladatList.push('feladat4')
    feladatList.push('feladat5')
  }

  const click = (element) => {
    props.feladatokClick(element)
  }

  return (
    <div>
      {makeFeladatList()}
      <div id="picker" className={props.feladatokVisibility}>
        <h2>Feladatok</h2>
        <div id = 'pickerFeladat'>
          {element = feladatList
            .map((feladat, index) => (
              <tr key={index} >
                <img src = {iconFeladat} alt = 'feladat1' width = '60vw' height = '80vh' onClick={(e) => click(element)}></img>
                <p>{index+1} feladat</p>
                </tr>
            ))}
        </div>
      </div>
    </div>
    );


}

export default Picker;
