import React, { useState, useEffect, useContext } from 'react'
import "./Feladat.css";
import EgyFeladat from './EgyFeladat'


let element
let cimMegoldas = []
let actulFeladatok = []

function Feladat(props) {


  const click = (element) => {
    props.feladatokClick(element)
  }

  const feladatCreator = (index) => {
    let tempList = []
    tempList = actualFeladatok[index].split('#')
    return tempList
  }

  const listOperations = () => {
    actualFeladatok.push(props.feladatok[0])
    actualFeladatok.push(props.feladatok[1])
    actualFeladatok.push(props.feladatok[2])
    actualFeladatok.push(props.feladatok[3])
    actualFeladatok.push(props.feladatok[4])
    cimMegoldas.push(props.feladatok[5])
    cimMegoldas.push(props.feladatok[6])
  }

  return (
    <div>
      {listOperations}
      <div id="feladat" className={props.feladatokVisibility}>
        <h2>{cimMegoldas[0]}</h2>
        <div id = 'pickerFeladat'>
          {props.actualFeladatok != undefined ? element = props.actualFeladatok.map((feladat, index) => (<ul key={index} ><EgyFeladat aFeladat = {feladatCreator(index)}/></ul>)) : null}
        </div>
      </div>
    </div>
    );


}

export default Feladat;
