import React, { useState, useEffect, useContext } from "react";
import { appContext } from '../App'
import './Nyit.css'
import BackgroundSlider from 'react-background-slider'
import alap1 from '../assets/alap1.jpg'
import alap2 from '../assets/alap2.jpg'


let pictures = []

function Nyit() {


console.log('login alap1 ', alap1)

  useEffect(() => {
      fillArray()
  }, [])

  const fillArray = () => {
    pictures.length = 0
        pictures.push(alap1)
        pictures.push(alap2)
  }

  return(
    <div>
      <BackgroundSlider
        id = 'base'
        images={[pictures]}
        duration={10} transition={2} />
    </div>
)
}


export default Nyit
