import React, { useState, useEffect, useContext } from 'react'
import "./Feladat.css";
import ButtonBack from '../buttons/ButtonBack'
import iconFeladat from '../../assets/iconFeladat.png'


let element

function Feladat(props) {


  const click = (element) => {
    props.feladatokClick(element)
  }

  return (
    <div>
      <div id="feladat" className={props.feladatokVisibility}>
        <h2>A feladat</h2>
        <div id = 'pickerFeladat'>
          {element = feladatPickerList
            .map((feladat, index) => (
              <ul key={index} >
                <img src = {iconFeladat} alt = 'feladat1' width = '60vw' height = '80vh' onClick={(e) => click(index)}></img>
                <p>{index+1} feladat</p>
                </ul>
            ))}
        </div>
      </div>
    </div>
    );


}

export default Feladat;
