import React, { useState, useEffect, useContext } from 'react'
import "./Feladat.css";
import EgyFeladat from './EgyFeladat'


let element
let cimMegoldas = []

function Feladat(props) {


  const click = (element) => {
    props.feladatokClick(element)
  }

  const feladatCreator = (index) => {
    let tempList = []
    tempList = props.feladatok[index].split('#')
    cimMegoldas.push(props.feladatok[5])
    cimMegoldas.push(props.feladatok[6])
    tempList.pop()
    tempList.pop()
    console.log('feladat tempList ', tempList)
    console.log('feladat cimMegoldas ', cimMegoldas)
    return tempList
  }

  return (
    <div>
      <div id="feladat" className={props.feladatokVisibility}>
        <h2>{props.feladatok[5]}</h2>
        <div id = 'pickerFeladat'>
          {props.feladatok != undefined ? element = props.feladatok.map((feladat, index) => (<ul key={index} ><EgyFeladat aFeladat = {feladatCreator(index)}/></ul>)) : null}
        </div>
      </div>
    </div>
    );


}

export default Feladat;
