import React, { useContext, useEffect } from "react";
import './Container.css'
import BackgroundSlider from 'react-background-slider'
import alap1 from '../assets/alap1.jpg'
import alap2 from '../assets/alap2.jpg'
import alap3 from '../assets/alap3.jpg'
import alap4 from '../assets/alap4.jpg'
import alap5 from '../assets/alap5.jpg'
import alap6 from '../assets/alap6.jpg'
import alap7 from '../assets/alap7.jpg'
import alap8 from '../assets/alap8.jpg'
import ButtonRolam from './buttons/ButtonRolam'
import ButtonFeladatok from './buttons/ButtonFeladatok'
import ButtonOktatas from './buttons/ButtonOktatas'
import ButtonVizsga from './buttons/ButtonVizsga'
import Feladatok from './sliders/Feladatok'
import { appContext } from '../App'


let pictures = []

  class Container extends Component {



    constructor(props) {
      super(props);
      this.state = {
        visible: false,
        visibleHely: false,
      };
      this.handleMouseDown = this.handleMouseDown.bind(this);
      this.handleMouseDownHely = this.handleMouseDownHely.bind(this);
      this.handleMouseDownAndroid = this.handleMouseDownAndroid.bind(this);
      this.toggleMenu = this.toggleMenu.bind(this);
    }

    fillArray(){
          pictures.length = 0
          pictures.push(alap1)
          pictures.push(alap2)
          pictures.push(alap3)
          pictures.push(alap4)
          pictures.push(alap5)
          pictures.push(alap6)
          pictures.push(alap7)
          pictures.push(alap8)
    }

    handleMouseDown(e) {
      this.toggleMenu();
      e.stopPropagation();
    }

    handleMouseDownHely(e) {
      this.toggleMenuHely();
      e.stopPropagation();
    }

    handleMouseDownAndroid(e) {
      console.log('SliderButtonAndroid')

      axios.get('https://szempont1.s3.eu-west-3.amazonaws.com/falugazdasz3_0.apk', {
        responseType: 'blob'
      })
      .then(response => {
        url = window.URL.createObjectURL(new Blob([response.data]))
        link = document.createElement('a')
        link.href = link
        link.setAttribute('download', 'falugazdasz.apk')
        document.body.appendChild(link)
        link.click()
      })
      .catch(error => {
        console.log(error)
      })

      e.stopPropagation();
    }

    toggleMenu() {
      this.setState({
        visible: !this.state.visible
      });
    }

    toggleMenuHely() {
      this.setState({
        visibleHely: !this.state.visibleHely
      });
    }

    render() {
      return (
        <div>
          {fillArray()}
          <Feladatok handleMouseDown={handleFeladatok} sliderVisibility={this.state.visible} />
          <ButtonRolam handleRolam={handleRolam}/>
          <ButtonFeladatok handleFeladatok={handleFeladatok}/>
          <ButtonOktatas handleOktatas={handleOktatas}/>
          <ButtonVizsga handleVizsga={handleVizsga}/>
          <BackgroundSlider
            images={[pictures[0], pictures[1], pictures[2], pictures[3], pictures[4], pictures[5], pictures[6], pictures[7]]}
            duration={10} transition={2} />
        </div>
      );
    }
  }

export default Container;
